import React, { Component } from 'react'

import '../styles/restaurant.css';

class PartnerEdit extends Component {

    constructor(props) {
        super(props)

        this.state = {
            partner: props.partner,
        }
    }

    componentWillReceiveProps(nextProps, nextContext) {
        if (nextProps.visible !== this.props.visible) {
            this.setState({
                partner: nextProps.partner,
            })
        }
    }

    submitEdit = (e) => {
        e.preventDefault()
        console.log('subbmit')
    }

    closeEdit = (e) => {
        e.preventDefault()

        this.props.closeEdit()
    }

    render() {
        console.log('edit state', this.state)

        if (this.props.visible) {

            return(
                <div >
                    <div
                        onClick={this.closeEdit}
                        className='restaurant-edit-backdrop'
                    />

                    <form
                        onSubmit={this.submitEdit}
                        className='restaurant-edit-container'
                    >
                        <div className={'restaurant-edit-title'}>
                            Edit
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Name:</label>
                            <input
                                className={'restaurant-edit-input'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>



                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Description:</label>
                            <textarea
                                className={'restaurant-edit-tainput'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>About:</label>
                            <textarea
                                className={'restaurant-edit-tainput'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Sustainability Practice:</label>
                            <textarea
                                className={'restaurant-edit-tainput'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Type of Rewards:</label>

                            <div className='restaurant-edit-input-checkbox'>
                                <div>
                                    <input type="checkbox" value="RewFD" ref="RewFDIN" onChange={this.handleChange}/> <span>Free Drink</span>
                                </div>
                                <div>
                                    <input type="checkbox" value="Rew10" ref="Rew10IN" onChange={this.handleChange}/> <span>10% Off</span>
                                </div>
                                <div>
                                    <input type="checkbox" value="Rew20" ref="Rew20IN" onChange={this.handleChange}/> <span>20% Off</span>
                                </div>
                                <div>
                                    <input type="checkbox" value="Rew25" ref="Rew25IN" onChange={this.handleChange}/> <span>25% Off</span>
                                </div>
                                <div>
                                    <input type="checkbox" value="Rew50" ref="Rew50IN" onChange={this.handleChange}/> <span>50% Off</span>
                                </div>
                                <div>
                                    <input type="checkbox" value="Rew75" ref="Rew75IN" onChange={this.handleChange}/> <span>75% Off</span>
                                </div>
                                <div>
                                    <input type="checkbox" value="RewFM" ref="RewFMIN" onChange={this.handleChange}/> <span>Free Meal</span>
                                </div>
                                <div>
                                    <input type="checkbox" value="Rew100" ref="Rew100IN" onChange={this.handleChange}/> <span>100% Off</span>
                                </div>
                            </div>
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Latitude:</label>
                            <input
                                ref="latIN"
                                placeholder="Latitude"
                                onChange={this.handleChange}
                                id={'small-input'}
                                required
                                type='number'
                                step={0.000001}
                            />
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Longitude:</label>
                            <input
                                ref="lngIN"
                                placeholder="Longitude"
                                onChange={this.handleChange}
                                id={'small-input'}
                                required
                                type='number'
                                step={0.000001}
                            />
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Address:</label>
                            <input
                                className={'restaurant-edit-input'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Phone:</label>
                            <input
                                className={'restaurant-edit-input'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Photo:</label>
                            <input
                                className={'restaurant-edit-input'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>

                        <div className='restaurant-edit-input-container'>
                            <label className='restaurant-edit-input-label'>Website (exclude 'http://www.')::</label>
                            <input
                                className={'restaurant-edit-input'}
                                placeholder={'First Name'}
                                type="text"
                                value={this.state.fname}
                                ref="fnameIN"
                                onChange={this.handleChange}/>
                        </div>


                        <div className={'restaurant-edit-button-container'}>
                            <button
                                className={'restaurant-edit-button-close'}
                                onClick={this.closeEdit}>Cancel
                            </button>
                            <button
                                className={this.state.formErrors
                                    ? 'restaurant-edit-button-disabled'
                                    : 'restaurant-edit-button' }
                            >Done
                            </button>
                        </div>



                    </form>
                </div>
            )
        } else {

            return(
                <div />

            )
        }
    }
}

export default PartnerEdit
