import React, { Component } from 'react'
import { withRouter } from "react-router-dom";


import NavBar from "../components/NavBar"

class RestaurantList extends Component {


    state = {
        items: [
            {
                name: "Starbucks",
                location: "MCG"
            },
            {
                name: "Anderson",
                location: "Home"
            },
            {
                name: "Candey",
                location: "House"
            },
            {
                name: "Danbury",
                location: "asd"
            },
            {
                name: "DistantLannd",
                location: "asdas"
            },
        ],
        itemsSorted: {}
    }

    logInUser = (user) => {
        this.props.logInUser(user)
    }

    logOutUser = () => {
        this.props.logOutUser()
    }

    goToProfile = () => {
        this.props.history.push('/user/' + this.props.user.name)
    }

    signUpUser = (user) => {
        this.props.signUpUser(user)
    }

    componentDidMount() {
        let temp =  {}
        this.state.items.forEach((item) => {
            let firstChar = item.name[0]
            if ( temp[firstChar.toUpperCase()] === undefined )
            temp[firstChar.toUpperCase()].push( item)
        })

        console.log('temp', temp)
    }


    render(){
        console.log(this.state)
        let sortedItems = this.state.items.sort((a, b) => (a.name > b.name) ? 1 : -1)
        console.log(sortedItems)

        let indexLetter = ''

        return(
            <div>
                <NavBar
                    {...this.props}
                    logOutUser={this.logOutUser}
                    logInUser={this.logInUser}
                    signUpUser={this.signUpUser}
                    userLoggedIn={this.props.userLoggedIn}
                    goToProfile={this.goToProfile}
                />

                <div className='restaurant-list-container'>
                {
                    sortedItems.map( item => {

                        if (indexLetter !== item.name[0].toUpperCase()){
                            indexLetter = item.name[0].toUpperCase()
                            return (
                                <div>
                                    <div className='restaurant-list-divider'>
                                        {item.name[0].toUpperCase()}
                                    </div>
                                    <div className='restaurant-list-item'>
                                        {item.name}
                                    </div>

                                </div>
                            )
                        }
                         else
                            return (

                                <div className='restaurant-list-item'>
                                    {item.name}
                                </div>
                            )
                    })

                }
                </div>



            </div>
        )
    }
}

export default withRouter(RestaurantList)
